INSERT INTO `acente_one_category` (`id`, `adi`, `aciklama`, `adi_tr`, `aciklama_tr`, `adi_ru`, `aciklama_ru`, `adi_esp`, `aciklama_esp`, `resim`, `resim_2`, `seo_adi`, `durum`, `anahtar`, `kisa_aciklama`, `one_cikan`, `one_cikan_otel`) VALUES
(4, 'Cappadocia', '<p>\n	Cappadocia has a charming and mysterious atmosphere with carved houses, churches, valleys and fairy chimneys.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;In the past, it was called &rdquo;Land of Beautiful Horses&rdquo;. You can enjoy this mysterious region by starting with underground cities, watching from colorful balloon tours and watching the sunset from the highest peak of Kızıl&ccedil;ukur valley.</p>\n', 'Kapadokya', '', 'Каппадокия', '', '', '', 'af9b0-mar-cerdeira-bbf9e2uuh88-unsplash.jpg', '04837-stocksnap_f90e2e0647.jpg', 'Cappadocia', 1, '<p>\n	hot air balloons, churches, fairy chimneys, cappadocia, cave, church, hot air balloon</p>\n', '<p>\n	Cappadocia has a charming and mysterious atmosphere with carved houses into the rocks, churches, valleys and fairy chimneys . In the past, it was called &rdquo;Land of Beautiful Horses&rdquo;. You can enjoy this mysterious region by starting with underground cities, watching from colorful balloon tours and watching the sunset from the highest peak of Kızıl&ccedil;ukur valley.</p>\n', 1, 1),
